<?php	/**	 * Classe ErrorController	 * @role: Affichage des erreurs levées lors de l'exécution	 * @creator: Balan David	 * @updated: 01/06/2013	**/	class ErrorController extends Controller{		function __construct(){			$this->acl = array(				'serverError'=>array('all'),				'notFound'=>array('all'),				'notAllowed'=>array('all'),				'all'=>array('all')			);		}		/**		 * Action appelée en cas d'erreur serveur (500)		**/		function serverError()		{			$this->useHelper('Html');			header("Status: 500 Internal Server Error");			$this->set('message', $this->params['message']);		}				/**		 * Action appelée si la page n'a pas pue ètre trouvée (404)		**/		function notFound()		{			$this->useHelper('Html');			header("Status: 404 Not Found");			$this->set('message', $this->params['message']);		}				function notAllowed()		{			$this->useHelper('Html');			header("Status: 401 Not Allowed");			$this->set('message', $this->params['message']);		}				/**		 * Action appelée pour tous les autres types d'erreur		**/		function all()		{			$this->useHelper('Html');			header("Status: 500 Internal Server Error");			$this->set('message', $this->params['message']);		}	}	