<?php	include MODELS.'Test.class.php';		class TestDao extends Dao{		function __construct(){			$this->useConnection = 'default';			$this->useTable = 'test';		}				function add(Test $test)		{			$con = $this->getConnection();			$con->prepare('INSERT INTO test(id, name) VALUES(NULL, :name)');			$con->exec(array(':name'=>$test->name));			$test->id = $con->lastInsertedId();		}				function load($id)		{			$con = $this->getConnection();			$this->useHelper('sql');			$request = $this->sql->createRequest()				->select(array('id', 'name'))				->from($this->useTable)				->where(array('id'=>$id));						$con = $this->getConnection();			$con->prepare($request);			$con->exec(array());						if($data = $con->read())				return new Test($data['id'], $data['name']);			return null;			//todo : test		}	}